default.recognizer.installation = "jar"
default.recognizer.version = "0.1.6"
default.recognizer.directory = "/etc/recognizer"
default.recognizer.user = "recognizer"

default.recognizer.librato.email = "EMAIL"
default.recognizer.librato.api_key = "API_KEY"
default.recognizer.librato.flush_interval = 10
default.recognizer.librato.metric_source = "recognizer"

default.recognizer.tcp.port = 2003
default.recognizer.tcp.threads = 20

default.recognizer.amqp.use_ssl = false
